import React from 'react';
import ReactDOM from 'react-dom';


const App = () => {
  const course = {
    name: 'Half Stack -sovelluskehitys',

    parts: [ 
    {
     name: 'Reactin perusteet',
     exercises: 10
    },
    {
      name :'Tiedonvälitys propseilla',
      exercises: 7
    },
    {
      name: 'Komponenttien tila',
      exercises : 14
    }
  ]
  }  
  return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts}/>
      <Total exercisess={course.parts} />
    </div>
  )
}

const Header = (name) => {
    console.log(name)
    return <h1>{name}</h1>

}

const Content = (parts) => {
    return (
        <div>
        <Part p1={parts[0]}/>
        <Part p1={parts[1]}/>
        <Part p3={parts[2]}/>
      </div>
    )
  }

const Total = (parts) => {
  return (
    <p>yhteensä {parts[0] + parts[1] + parts[2]} tehtävää</p>
  )
}

const Part = (args) => {
    return (
      <div>
          <p>
              {args.name} {args.exercises}
          </p>
      </div>
    )
  }

ReactDOM.render(<App />, document.getElementById('root'))
